    { value: 'strawberry', label: 'Strawberry' },
    { value: 'vanilla', label: 'Vanilla' }
  ]

  return (
    <>
        <Select
          className="basic-single"
          classNamePrefix="select"
          defaultValue={"nulo"}
          isClearable={true}
          isSearchable={true}
          name="color"
          onChange={console.log(Select.color.getValue)}
          options={options}
        />

